struct CaptureSessionResult: Codable, Hashable {
    var uuid: UUID
    var timestamp: Date
    var sessionStart: Date
    var sessionEnd: Date
}

enum CaptureError: Error {
    case noStart
    case conflict
}

struct ShelfScanModuleCapture: Hashable, Codable, Identifiable, Comparable {
    static func < (lhs: ShelfScanModuleCapture, rhs: ShelfScanModuleCapture) -> Bool {
        guard lhs.moduleOrder == rhs.moduleOrder else {
            return (lhs.moduleOrder ?? .max) < (rhs.moduleOrder ?? .max)
        }

        let lhsRef = lhs.moduleIdentity.moduleReference
        let rhsRef = rhs.moduleIdentity.moduleReference

        switch lhsRef.compare(rhsRef, options: .numeric) {
        case .orderedDescending, .orderedSame:
            return false
        case .orderedAscending:
            return true
        }
    }

    static var now: () -> Date = { .now }

    static var uuid: () -> UUID = { .init() }

    enum Error: Swift.Error {
        case alreadyFinalized
    }

    var id: UUID { uuid }

    var type: Capture.SessionType = .ar

    var configVersion: Int = 1

    var uuid: UUID = Self.uuid()

    let moduleIdentity: ModuleIdentity

    let moduleOrder: Int?

    let moduleId: Int?

    var sessionStart = Self.now()

    var captureId: UUID { uuid }

    var result: CaptureSessionResult?

    var cameraType: MetadataCameraType {
        capturedImages.first?.metadata.cameraType ?? .defaultType
