    private static let actionPublisher = PassthroughSubject<Action, Never>()

    @Dependency(\.networkStatusMonitorService)
    private var networkStatusMonitor

    @Dependency(\.firebaseAnalytics)
    private var analytics

    @Dependency(\.modulePreviewRepository)
    private var previewRepository: ModulePreviewRepository

    @Dependency(\.keyValueStorage)
    private var keyValueStorage

    private let navigation: Navigation

    init(navigation: Navigation) {
        self.navigation = navigation
    }

    @MainActor
    var body: some ReducerOf<Self> {
        Reduce { state, action in
            switch action {
            case .onAppear:
                return .concatenate([
                    .send(.loadHandednessPreference),
                    .send(.analytics(event: .moduleSelectionScreen)),
                    .send(.updateModuleThumbnails),
                    .publisher { Self.actionPublisher }
                        .cancellable(id: CancelID.actionPublisher),
