import Combine
import ComposableArchitecture
import Dependencies
import Foundation
import UIKit

@Reducer
struct UpdateFlowAislePreviewFeature {
    @ObservableState
    struct State: Equatable {
        let aisle: PersistedShelfScanAisle

        var firstModuleImage: Refreshable<UIImage> = .idle
        var lastModuleImage: Refreshable<UIImage> = .idle
        var captureDirection: ModuleCaptureOrder = .leftToRight
        var handednessPreference: HandednessPreference = .rightHanded
        var noNetworkConnectionToast: ToastMessage?
        var noNetworkConnectionDialog: ConfirmationDialogViewModel?
        var networkStatus: NetworkStatusFeature.State = .init(config: .aislePreview)

        var firstModule: PersistedShelfScanModule? {
            aisle.modules.first
        }

        var lastModule: PersistedShelfScanModule? {
            aisle.modules.count > 1 ? aisle.modules.last : nil
        }

        var aisleName: String {
            aisle.name
        }

        var recommendedCaptureOrder: ModuleCaptureOrder {
            handednessPreference.defaultCaptureOrder
        }

        var isFirstModuleRecommended: Bool {
            recommendedCaptureOrder == .leftToRight
        }

        var isLastModuleRecommended: Bool {
            recommendedCaptureOrder == .rightToLeft
        }

        init(aisle: PersistedShelfScanAisle) {
            self.aisle = aisle
        }
    }

    enum Action {
        case onAppear
        case onDisappear
        case onBackButtonTapped
        case refreshImage
        case onSelectModule(direction: ModuleCaptureOrder)
        case onHandednessPreferenceSelected(HandednessPreference)
        case loadHandednessPreference
        case updateModuleThumbnails
        case setFirstModuleImage(Refreshable<UIImage>)
        case setLastModuleImage(Refreshable<UIImage>)
        case showNoNetworkConnectionToast
        case showNoNetworkConnectionDialog
        case showNoNetworkConnectionToastIfNeeded([Error])
        case hideToast
        case hideDialog
        case navigateToModule(AisleCaptureModel)
        case navigateToOfflineAvailability
        case networkStatus(NetworkStatusFeature.Action)
        case analytics(event: UpdateFlowFirebaseEvent)
    }

    private enum CancelID {
        case moduleThumbnails
        case actionPublisher
    }

    // Issue SHELF-19080: I don't know why, but the action publisher is recreated and we don't receive values
    private static let actionPublisher = PassthroughSubject<Action, Never>()

    @Dependency(\.networkStatusMonitorService)
    private var networkStatusMonitor

    @Dependency(\.firebaseAnalytics)
    private var analytics

    @Dependency(\.modulePreviewRepository)
    private var previewRepository: ModulePreviewRepository

    @Dependency(\.keyValueStorage)
    private var keyValueStorage

    private let navigation: Navigation

    init(navigation: Navigation) {
        self.navigation = navigation
    }

    @MainActor
    var body: some ReducerOf<Self> {
        Reduce { state, action in
            switch action {
            case .onAppear:
                return .concatenate([
                    .send(.loadHandednessPreference),
                    .send(.analytics(event: .moduleSelectionScreen)),
                    .send(.updateModuleThumbnails),
                    .publisher { Self.actionPublisher }
                        .cancellable(id: CancelID.actionPublisher),
                ])

            case .loadHandednessPreference:
                let savedPreference: HandednessPreference? = keyValueStorage[.handednessPreference]
                state.handednessPreference = savedPreference ?? .rightHanded
                return .none

            case .onHandednessPreferenceSelected(let preference):
                state.handednessPreference = preference
                keyValueStorage[.handednessPreference] = preference
                return .send(.analytics(event: .handednessPreferenceSelected(preference)))

            case .onBackButtonTapped:
                navigation.pop()
                return .none

            case .refreshImage:
                guard !state.firstModuleImage.isLoading else {
                    return .none
                }

                guard networkStatusMonitor.status.isOnline else {
                    return .send(.showNoNetworkConnectionToast)
                }

                return .send(.updateModuleThumbnails)

            case .onSelectModule(let direction):
                let analyticsEvent: UpdateFlowFirebaseEvent =
                    direction == .leftToRight
                    ? .moduleStartSelectionFirst : .moduleStartSelectionLast

                let model = AisleCaptureModel(
                    aisle: state.aisle,
                    moduleOrder: direction
                )

                return .concatenate([
                    .send(.analytics(event: analyticsEvent)),
                    .send(.navigateToModule(model)),
                ])

            case .updateModuleThumbnails:
                let previewRepository = previewRepository
                return .run { [aisle = state.aisle] send in
                    var errors: [Error] = []

                    await withTaskGroup(of: Void.self) { group in
                        let modulePreviewHelper = ModulePreviewHelper(
                            previewRepository: previewRepository,
                            ignoreOfflineStatus: true
                        )

                        // Load first module thumbnail
                        group.addTask {
                            await modulePreviewHelper.reloadPreview(
                                moduleId: aisle.modules.first?.id,
                                onStatusUpdated: { image in
                                    Task {
                                        await send(.setFirstModuleImage(image))
                                    }
                                }
                            )
                        }

                        // Load last module thumbnail
                        group.addTask {
                            await modulePreviewHelper.reloadPreview(
                                moduleId: aisle.modules.count > 1 ? aisle.modules.last?.id : nil,
                                onStatusUpdated: { image in
                                    Task {
                                        await send(.setLastModuleImage(image))
                                    }
                                }
                            )
                        }
                    }

                    await send(.showNoNetworkConnectionToastIfNeeded(errors))

                    // Preload remaining module previews
                    await withThrowingTaskGroup(of: Void.self) { group in
                        for moduleId in aisle.modules.removingFirstAndLast().map(\.id) {
                            group.addTask {
                                _ = try await previewRepository.loadPreview(forModuleId: moduleId)
                            }
                        }
                    }
                }
                .cancellable(id: CancelID.moduleThumbnails)

            case .setFirstModuleImage(let image):
                state.firstModuleImage = image
                return .none

            case .setLastModuleImage(let image):
                state.lastModuleImage = image
                return .none

            case .showNoNetworkConnectionToastIfNeeded(let errors):
                let isNoNetworkConnectionError = errors.first(
                    where: { $0.urlErrorCode == .notConnectedToInternet }
                ).isNotNil
                guard isNoNetworkConnectionError else { return .none }

                return .send(.showNoNetworkConnectionToast)

            case .showNoNetworkConnectionToast:
                state.noNetworkConnectionToast = .warn(
                    L10n.UpdateFlow.AislePreview.Toast.noNetworkConnection,
                    icon: Images.Offline.noInternetConnection,
                    button: .init(
                        text: L10n.UpdateFlow.AislePreview.Toast.noNetworkConnectionButton,
                        action: {
                            Self.actionPublisher.send(.showNoNetworkConnectionDialog)
                        }
                    )
                )
                return .none

            case .showNoNetworkConnectionDialog:
                state.noNetworkConnectionDialog = .init(
                    title: L10n.UpdateFlow.AislePreview.NoNetworkConnectionDialog.title,
                    description: L10n.UpdateFlow.AislePreview.NoNetworkConnectionDialog.description,
                    primaryButton: .init(
                        title: L10n.UpdateFlow.AislePreview.NoNetworkConnectionDialog.downloadButton,
                        style: .secondary,
                        action: {
                            Self.actionPublisher.send(.navigateToOfflineAvailability)
                        }
                    )
                )
                return .none

            case .hideToast:
                state.noNetworkConnectionToast = nil
                return .none

            case .hideDialog:
                state.noNetworkConnectionDialog = nil
                return .none

            case .navigateToOfflineAvailability:
                navigation.push(OfflineAvailabilityNavigationDestination())
                return .none

            case .networkStatus:
                return .none

            case .analytics(let event):
                analytics.logUpdateFlow(event: event)
                return .none
            case .onDisappear, .navigateToModule:
                return .none
            }
        }
        Scope(state: \.networkStatus, action: \.networkStatus) {
            NetworkStatusFeature()
        }
    }
}

