// Copyright © 2025 Scandit. All rights reserved.

import Foundation

class ModulePreviewHelper {
    private let previewRepository: ModulePreviewRepository
    private var reloadTask: Task<Void, Never>?
    private let fetchTimeout: Duration
    private let cacheUpdateTimeout: Duration
    private let ignoreOfflineStatus: Bool

    init(
        previewRepository: ModulePreviewRepository,
        ignoreOfflineStatus: Bool = false,
        fetchTimeout: Duration = .seconds(5),
        cacheUpdateTimeout: Duration = .seconds(2)
    ) {
        self.previewRepository = previewRepository
        self.ignoreOfflineStatus = ignoreOfflineStatus
        self.fetchTimeout = fetchTimeout
        self.cacheUpdateTimeout = cacheUpdateTimeout
    }

    @MainActor
    func reloadPreview(
        moduleId: Int?,
        onStatusUpdated: @escaping (Refreshable<UIImage>) -> Void
    ) async {
        guard let moduleId else {
            onStatusUpdated(.idle)
            return
        }

        reloadTask?.cancel()
        let reloadTask = Task {
            var lastLoaded: UIImage?
            do {
                let (cached, loadingTask) = try await previewRepository.preview(forModuleId: moduleId)
                lastLoaded = cached
                let timeout: Duration
                if let cached {
                    onStatusUpdated(.refreshing(current: cached))
                    timeout = cacheUpdateTimeout
                } else {
                    onStatusUpdated(.loading)
                    timeout = fetchTimeout
                }

                try onStatusUpdated(
                    .success(await loadingTask.withTimeout(timeout).value)
                )
            } catch is TimeoutError {
                if let lastLoaded {
                    onStatusUpdated(.success(lastLoaded))
                } else {
                    onStatusUpdated(.failure)
                }
            } catch ModulePreviewError.networkUnavailable {
                // If network not available, try showing the last available image.
                if let lastLoaded {
                    onStatusUpdated(.success(lastLoaded))
                } else if ignoreOfflineStatus {
                    onStatusUpdated(.idle)
                } else {
                    onStatusUpdated(.failure)
                }
            } catch {
                onStatusUpdated(.failure)
            }
        }
        self.reloadTask = reloadTask
        return await reloadTask.value
    }
}

