// Copyright © 2025 Scandit. All rights reserved.

import Foundation

enum ShelfScanModule: Hashable {
    case existing(details: PersistedShelfScanModule)
    case pending(module: PendingShelfScanModule)

    func isEqualTo(_ other: Self) -> Bool {
        switch (self, other) {
        case let (.existing(lhs), .existing(rhs)):
            lhs.id == rhs.id &&
                lhs.storeId == rhs.storeId &&
                lhs.reference == rhs.reference
        case let (.pending(lhs), .pending(rhs)):
            lhs.moduleIdentity == rhs.moduleIdentity
        default:
            false
        }
    }
}

extension ShelfScanModule {
    var moduleReference: String {
        switch self {
        case let .existing(module):
            module.reference
        case let .pending(module):
            module.moduleReference
        }
    }

    var moduleId: Int? {
        switch self {
        case let .existing(module):
            module.id
        case .pending:
            nil
        }
    }

    var storeIdentifier: Int {
        switch self {
        case let .existing(module):
            module.storeId
        case let .pending(module):
            module.store
        }
    }

    var lastUpdated: Date? {
        switch self {
        case let .existing(module):
            module.lastUpdated
        case .pending:
            nil
        }
    }

    var order: Int? {
        switch self {
        case let .existing(module):
            module.order
        case let .pending(module):
            module.order
        }
    }
}

class PendingShelfScanModule: Hashable {
    let store: Int
    let aisle: PersistedShelfScanAisle
    let moduleReference: String
    let order: Int

    init(
        store: Int,
        aisle: PersistedShelfScanAisle,
        moduleReference: String,
        order: Int
    ) {
        self.store = store
        self.aisle = aisle
        self.moduleReference = moduleReference
        self.order = order
    }

    static func == (lhs: PendingShelfScanModule, rhs: PendingShelfScanModule) -> Bool {
        lhs.store == rhs.store && lhs.aisle.name == rhs.aisle.name && lhs
            .moduleReference == rhs.moduleReference
    }

    func hash(into hasher: inout Hasher) {
        hasher.combine(store)
        hasher.combine(aisle.name) // Aisle names are unique
        hasher.combine(moduleReference)
    }
}

extension PendingShelfScanModule {
    var moduleIdentity: ModuleIdentity {
        ModuleIdentity(
            aisle: aisle,
            moduleId: nil,
            moduleReference: moduleReference
        )
    }
}

