// Copyright © 2025 Scandit. All rights reserved.

import Foundation

/// A type that represents the state of a loadable and refreshable resource.
enum Refreshable<Value> {
    /// The resource is idle.
    case idle
    /// The resource is loading.
    case loading
    /// The resource has been loaded successfully.
    case success(Value)
    /// The resource has been loaded and is currently refreshing.
    case refreshing(current: Value)
    /// The resource failed to load.
    case failure

    var isIdle: Bool {
        if case .idle = self {
            return true
        }
        return false
    }

    var isLoading: Bool {
        switch self {
        case .loading, .refreshing:
            true
        case .idle, .failure, .success:
            false
        }
    }

    var isRefreshing: Bool {
        if case .refreshing = self {
            return true
        }
        return false
    }

    var isSuccess: Bool {
        switch self {
        case .success, .refreshing:
            true
        case .idle, .failure, .loading:
            false
        }
    }

    var isFailure: Bool {
        if case .failure = self {
            return true
        }
        return false
    }

    var value: Value? {
        switch self {
        case let .success(value), let .refreshing(value):
            value
        case .idle, .failure, .loading:
            nil
        }
    }
}

extension Refreshable: Equatable where Value: Equatable {}

extension Refreshable: Hashable where Value: Hashable {}

