import ComposableArchitecture

@Reducer
struct MappingModuleListFeature {
    @ObservableState
    struct State: Equatable {
        @Presents var editAisleName: MappingModuleListEditAisleNameFeature.State?

        var list: AisleModuleListFeature.State

        var toast: ToastMessage?

        var aisle: PersistedShelfScanAisle {
            list.aisle
        }

        var name: String {
            list.aisleName
        }
    }

    enum Action {
        case list(AisleModuleListFeature.Action)
        case editAisleName(PresentationAction<MappingModuleListEditAisleNameFeature.Action>)
        case onEditAisleNameButtonTapped
        case onToastDismiss(ToastMessage?)
    }

    let useCase: AnyMappingModuleListUseCase

    init(useCase: AnyMappingModuleListUseCase = MappingModuleListUseCase()) {
        self.useCase = useCase
    }

    @MainActor
    var body: some Reducer<State, Action> {
        Reduce { state, action in
            switch action {
            case .onEditAisleNameButtonTapped:
                state.editAisleName = .init(
                    aisle: state.aisle,
                    isNamingConventionGuideEnabled: useCase.isNamingConventionGuideEnabled
                )
                return .none
            case .editAisleName(.presented(.onCancelTapped)):
                state.editAisleName = nil
                return .none
            case .editAisleName(.presented(.handleSuccessfulUpdate(let name))):
                state.editAisleName = nil
                state.list = .init(
                    aisle: state.list.aisle.updating(name: name),
                    modules: state.list.modules,
                    didAppear: state.list.didAppear,
                    allowsModuleCreation: state.list.allowsModuleCreation
                )
                state.toast = .success(L10n.MappingFlow.EditAisleName.toastSuccess)
                return .none
            case .onToastDismiss:
                state.toast = nil
                return .none
            case .list, .editAisleName:
                return .none
            }
        }
        .ifLet(\.$editAisleName, action: \.editAisleName) {
            MappingModuleListEditAisleNameFeature()
        }

        Scope(state: \.list, action: \.list) {
            AisleModuleListFeature()
        }
    }
}

