// Copyright  2023 Scandit. All rights reserved.

import Combine
import Dependencies
import SwiftUI

@MainActor
protocol Navigation {
    func push(destination: NavigationDestination)
    func pop(to dest: NavigationDestination)
    func popToRoot()

    func push(_ data: any Hashable)
    func pop(_ times: Int)

    func contains(_ data: any Hashable) -> Bool
    func isLast(_ destination: NavigationDestination) -> Bool
    func tagLast(destination: NavigationDestination)
}

extension Navigation {
    func pop() {
        pop(1)
    }

    func contains(destination: NavigationDestination) -> Bool {
        contains(destination)
    }

    @MainActor
    func isPushedOnStack(_ destination: NavigationDestination) -> Bool {
        isLast(destination)
    }

    @MainActor
    func isPoppedFromStack(_ destination: NavigationDestination) -> Bool {
        !contains(destination)
    }
}

struct NavigationDestination: Hashable, CustomStringConvertible {
    static func == (lhs: Self, rhs: Self) -> Bool {
        lhs.id == rhs.id && lhs.name == rhs.name
    }

    let id: UUID
    let name: String
    fileprivate var index = 0

    var description: String {
        "\(id.uuidString) - \(name) - \(index)"
    }

    init(name: String) {
        id = .init()
        self.name = name
    }

    func hash(into hasher: inout Hasher) {
        hasher.combine(id)
    }
}

private extension NavigationDestination {
    init(id: UUID, name: String, index: Int) {
        self.id = id
        self.name = name
        self.index = index
    }

    func updating(index: Int) -> Self {
        .init(id: id, name: name, index: index)
    }
}

class NavigationStore: ObservableObject, Navigation {
    static var shared: NavigationStore = .init()
    static var route: NavigationStore = .init()

    @Published var path: NavigationPath = .init()

    private(set) var storage: Set<NavigationDestination> = []
    private var allPaths: [any Hashable] = []

    private var cancellables: Set<AnyCancellable> = []

    init() {
        $path.weakSink(on: self) { root, path in
            root.storage = root.storage.filter { $0.index < path.count }

            guard root.allPaths.count > path.count else { return }

            root.allPaths.removeLast(
                root.allPaths.count - path.count
            )
        }.store(in: &cancellables)
    }

    @MainActor
    func push(_ data: any Hashable) {
        guard allPaths.last?.hashValue != data.hashValue else { return }

        // Auto-dismiss any open menu when navigation occurs
        MenuPresenter.shared.dismiss()

        path.append(data)
        allPaths.append(data)
    }

    @MainActor
    func push(destination: NavigationDestination) {
        guard !contains(destination) else { return }

        storage.insert(destination.updating(index: path.count))
        push(destination)
    }

    @MainActor
    func pop(_ times: Int = 1) {
        guard !path.isEmpty else { return }
        guard times >= 0 else {
            preconditionFailure("The pop times value can't be lower than 0")
        }

        path.removeLast(Int(times))
    }

    @MainActor
    func pop(to dest: NavigationDestination) {
        guard let index = storage.first(where: { $0.id == dest.id })?.index else {
            assertionFailure("Destination does not exists - \(dest)")
            return
        }

        pop(path.count - index - 1)
    }

    @MainActor
    func popToRoot() {
        path.removeLast(path.count)
    }

    @MainActor
    func tag(destination: NavigationDestination, at index: Int) {
        storage.insert(destination.updating(index: index))
    }

    @MainActor
    func tagLast(destination: NavigationDestination) {
        guard !storage.contains(destination) else { return }

        storage.insert(destination.updating(index: path.count - 1))
    }

    @MainActor
    func contains(_ data: any Hashable) -> Bool {
        allPaths.contains(where: { $0.hashValue == data.hashValue })
    }

    @MainActor
    func isLast(_ destination: NavigationDestination) -> Bool {
        let val = storage.first(where: { $0 == destination })
        return val?.index == path.count - 1
    }
}

// MARK: - Preview Implementation

#if DEBUG
@MainActor
struct FakeNavigation: Navigation {
    func push(destination _: NavigationDestination) {}
    func pop(to _: NavigationDestination) {}
    func popToRoot() {}
    func push(_: any Hashable) {}
    func pop(_: Int) {}
    func tagLast(destination _: NavigationDestination) {}
    func contains(_ data: any Hashable) -> Bool { false }
    func isLast(_: NavigationDestination) -> Bool { false }
}
#endif

