// Copyright  2024 Scandit. All rights reserved.

import ComposableArchitecture

@Reducer
struct MappingModeModulePreviewFeature {
    @ObservableState
    struct State: Equatable {
        let module: ShelfScanModule
        let aisle: PersistedShelfScanAisle
        let isRetaking: Bool

        var title: String {
            "Module \(aisle.name) - \(module.moduleReference)"
        }

        var moduleGroupName: String {
            aisle.name
        }
    }

    enum Action {
        case onAppear
        case onContinueButtonTapped
        case viewDisappeared
    }

    @Dependency(\.firebaseAnalytics)
    var analytics

    @MainActor
    var body: some Reducer<State, Action> {
        Reduce { state, action in
            switch action {
            case .onAppear:
                analytics.logMappingFlow(event: .modulePreviewScreen)
                return .none

            case .onContinueButtonTapped:
                return .none

            case .viewDisappeared:
                return .none
            }
        }
    }
}

// Copyright  2024 Scandit. All rights reserved.

import ComposableArchitecture
import SwiftUI

struct MappingModeModulePreviewView: View {
    let store: TCAStoreOf<MappingModeModulePreviewFeature>

#if DEBUG
    @ObserveInjection private var forceRedraw
#endif

    var body: some View {
        VStack(spacing: 0) {
            ScrollView {
                VStack(spacing: 24) {
                    // Module Image Placeholder
                    VStack(spacing: 16) {
                        Text(L10n.MappingFlow.ModulePreview.moduleImage)
                            .defaultFont(size: 16, weight: .semibold)
                            .foregroundStyle(Colors.gray90)
                            .frame(maxWidth: .infinity, alignment: .leading)

                        ZStack {
                            RoundedRectangle(cornerRadius: 12)
                                .strokeBorder(
                                    Colors.gray40,
                                    style: StrokeStyle(lineWidth: 2, dash: [8, 8])
                                )
                                .frame(height: 200)
                                .background(Colors.gray10)
                                .cornerRadius(12)

                            Image(systemName: "plus")
                                .font(.system(size: 48, weight: .light))
                                .foregroundStyle(Colors.gray60)
                        }
                    }

                    // Module Group
                    VStack(spacing: 8) {
                        Text(L10n.MappingFlow.ModulePreview.moduleGroup)
                            .defaultFont(size: 16, weight: .semibold)
                            .foregroundStyle(Colors.gray90)
                            .frame(maxWidth: .infinity, alignment: .leading)

                        Text(store.moduleGroupName)
                            .defaultFont(size: 16, weight: .regular)
                            .foregroundStyle(Colors.gray70)
                            .frame(maxWidth: .infinity, alignment: .leading)
                            .padding(.horizontal, 16)
                            .padding(.vertical, 12)
                            .background(Colors.gray20)
                            .cornerRadius(8)
                    }
                }
                .padding(.horizontal, 16)
                .padding(.top, 24)
            }

            // Continue Button
            Button {
                store.send(.onContinueButtonTapped)
            } label: {
                Text(L10n.MappingFlow.ModulePreview.continueButton)
                    .frame(maxWidth: .infinity)
            }
            .buttonStyle(ScanditButtonStyle(style: .primary))
            .padding(.horizontal, 16)
            .padding(.vertical, 16)
        }
        .scanditNavigationBar(title: store.title)
        .toolbar(.hidden, for: .tabBar)
        .onAppear {
            store.send(.onAppear)
        }
        .onDisappear {
            store.send(.viewDisappeared)
        }
        .enableInjection()
    }
}

