import ComposableArchitecture
import Dependencies
import Introspect
import SwiftUI

extension NavigationDestination {
    static let slowFlowRootView = Self(name: "OnboardingFlowRootView")
}

struct OnboardingFlowRootView: View {
    @Dependency(\.homeNavigation)
    var navigation

    let store: TCAStoreOf<OnboardingFlowRootViewStore>

    @Dependency(\.aisleService)
    var aisleService

    let storeId: Int

    let storeName: String

    init(
        store: Store,
        licenseKey: String?,
        settings: OnboardingFlowSettings,
        exportOptions: ShelfScanCaptureExportOptions
    ) {
        self.storeId = store.id
        self.storeName = store.name
        self.store = ComposableArchitecture.Store(
            initialState: OnboardingFlowRootViewStore.State(store: store)
        ) {
            OnboardingFlowRootViewStore(
                licenseKey: licenseKey,
                settings: settings,
                exportOptions: exportOptions,
                store: store
            )
        }
    }

    var body: some View {
        AisleListView(
            store: store.scope(state: \.aisleList, action: \.aisleList)
        )
        .onAppear {
            navigation.tagLast(destination: .slowFlowRootView)
            store.send(.onAppear)
        }
        .navigationDestination(for: NavigationDestination.self) { destination in
            switch destination {
            case .aisleModuleListView:
                // NOTE: Logic doesn't belong into view. Module list needs refactoring
                // to support multiple styles.
                store.optionalScope(
                    state: \.moduleList,
                    action: \.moduleList,
                    view: AisleModuleListView.create(
                        moduleDescriptionFor: { module, aisle in
                            aisleService
                                .localUpdateDate(
                                    forModule: ModuleIdentity(
                                        aisle: aisle,
                                        moduleId: module.id,
                                        moduleReference: module.reference
                                    ),
                                    ofSessionTypes: .onboardingFlow
                                )?
                                .lastScannedDescription
                        },
                        shouldShowCheckmarkFor: { module, aisle in
                            @Dependency(\.settingsManager)
                            var manager

                            return aisleService
                                .localUpdateDate(
                                    forModule: ModuleIdentity(
                                        aisle: aisle,
                                        moduleId: module.id,
                                        moduleReference: module.reference
                                    ),
                                    ofSessionTypes: .onboardingFlow
                                ).flatMap {
                                    $0.timeIntervalSinceNow < manager.checkmarkCompletionOnboardingPeriod
                                } ?? false
                        }
                    )
                )
                .scanditNavigationBar()
            case .selectedModuleView:
                store.optionalScope(
                    state: \.modulePreview,
                    action: \.modulePreview,
                    view: OnboardingFlowModulePreviewView.init
                )
                .scanditNavigationBar()
            case .slowFlowCapture:
                store.optionalScope(
                    state: \.capture,
                    action: \.capture,
                    view: OnboardingFlowCaptureView.init
                )
                .toolbarBackground(.hidden, for: .navigationBar)
            case .slowFlowCaptureReview:
                store.optionalScope(
                    state: \.captureReview,
                    action: \.captureReview,
                    view: OnboardingFlowCaptureReviewView.init
                )
                .scanditNavigationBar()
            default:
                EmptyView()
            }
        }
        .scanditNavigationBar(title: L10n.Svm.OnboardingFlow.List.title)
        .scanditNavigationBar()
        .toolbar(.hidden, for: .tabBar)
    }
}

