import ComposableArchitecture
import SwiftUI

struct MappingModuleListView: View {
    @Bindable
    var store: TCAStoreOf<MappingModuleListFeature>

#if DEBUG
    @ObserveInjection private var forceRedraw
#endif

    var body: some View {
        VStack {
            HStack(spacing: 12) {
                Text(store.name)
                    .defaultFont(size: 20, weight: .semibold)
                    .foregroundStyle(Colors.gray90)
                    .frame(maxWidth: .infinity, alignment: .leading)

                Button {
                    store.send(.onEditAisleNameButtonTapped)
                } label: {
                    Images.editPencil.swiftui
                }
            }
            .padding(.horizontal, 12)
            .padding(.vertical, 23)

            AisleModuleListView.create()(store.scope(state: \.list, action: \.list))
        }
        .toast(
            $store.toast.sending(\.onToastDismiss),
            config: .init(alignment: .bottom, verticalPadding: 96)
        )
        .scanditNavigationBar(title: L10n.MappingFlow.List.title)
        .sheet(item: $store.scope(state: \.editAisleName, action: \.editAisleName), content: { val in
            MappingModuleListEditAisleNameView(store: val)
                .presentationDetents([.fraction(0.95)])
        })
        .enableInjection()
    }
}

