// Copyright © 2024 Scandit. All rights reserved.

import Foundation

struct PersistedShelfScanAisle: Hashable, Codable {
    var id: Int
    var name: String
    var store: Int
    var lastUpdated: Date?
    var modules: [PersistedShelfScanModule]

    func updating(name: String) -> PersistedShelfScanAisle {
        var val = self
        val.name = name
        return val
    }

}

struct PersistedShelfScanModule: Hashable, Codable, Identifiable {
    var id: Int
    var storeId: Int
    var reference: String
    var order: Int?
    var lastUpdated: Date?
}

extension PersistedShelfScanModule: Comparable {
    static func < (lhs: PersistedShelfScanModule, rhs: PersistedShelfScanModule) -> Bool {
        guard lhs.order == rhs.order else {
            return (lhs.order ?? .max) < (rhs.order ?? .max)
        }

        switch lhs.reference.compare(rhs.reference, options: .numeric) {
        case .orderedDescending, .orderedSame:
            return false
        case .orderedAscending:
            return true
        }
    }
}

extension [PersistedShelfScanModule] {
    func mergingByReference() -> [Element] {
        let grouped = Dictionary(grouping: self, by: { $0.reference })
        return grouped.compactMap { moduleReference, modulesWithSameName in
            guard let existingWithId = modulesWithSameName.latest() else {
                return nil
            }
            let existingOrderNumber = modulesWithSameName.first?.order ?? 0
            return PersistedShelfScanModule(
                id: existingWithId.id,
                storeId: existingWithId.storeId,
                reference: moduleReference,
                order: existingOrderNumber,
                // Consider the latest date of any given module.
                lastUpdated: modulesWithSameName.compactMap(\.lastUpdated).sorted().last
            )
        }
    }

    private func latest() -> Element? {
        sorted(by: {
            ($0.lastUpdated ?? .distantPast) < ($1.lastUpdated ?? .distantPast)
        }).last
    }

    func sortedByReference() -> Self {
        sorted(by: { $0.reference < $1.reference })
    }

    func sortedByNumericReference() -> Self {
        sorted(by: { $0.reference.integerValue < $1.reference.integerValue })
    }
}

extension [ShelfScanAisleDetails] {
    func filter(includeEmpty: Bool) -> Self {
        guard !includeEmpty else { return self }
        return filter { aisleDetails in
            aisleDetails.aisle.modules.count > 0
        }
    }
}

extension [PersistedShelfScanAisle] {
    func filter(includeEmpty: Bool) -> Self {
        guard !includeEmpty else { return self }
        return filter { aisle in
            aisle.modules.count > 0
        }
    }
}

