    accessibilityViewFor: @escaping (PersistedShelfScanModule, PersistedShelfScanAisle) -> AnyView? = Self.defaultAccessibilityView
) -> (TCAStoreOf<AisleModuleListFeature>) -> AisleModuleListView

// Dodaj property do struktury
