// Copyright © 2024 Scandit. All rights reserved.

import ComposableArchitecture
import OSLog

extension NavigationDestination {
    static let selectedModuleView = Self(name: "SelectedModuleView")
}

@Reducer
struct AisleModuleListFeature {
    @ObservableState
    struct State: Equatable {
        let aisle: PersistedShelfScanAisle
        var modules: [PersistedShelfScanModule]
        var didAppear = false
        var allowsModuleCreation = true

        var aisleName: String {
            aisle.name
        }
    }

    enum Action {
        case onInit
        case onDeinit

        case onAppear
        case onDisappear

        case onError(String)

        case onModuleButtonTapped(module: PersistedShelfScanModule)
        case onAddModuleButtonTapped

        case createNewModule
        case onModuleCreated(aisle: PersistedShelfScanAisle, module: ShelfScanModule)
        case onModuleSelected(aisle: PersistedShelfScanAisle, module: PersistedShelfScanModule)

        case subscribeToModules
        case updateModules(modules: [PersistedShelfScanModule], aisleId: Int)
    }

    @Dependency(\.homeNavigation)
    var navigation

    @Dependency(\.firebaseAnalytics)
    var analytics

    @Dependency(\.aisleService)
    var aisleService

    private enum CancelID { case module, list }

    @MainActor
    var body: some Reducer<State, Action> {
        Reduce { state, action in
            switch action {
            case .onInit, .onDeinit:
                return .none
            case .onAppear:
                analytics.logMappingFlow(event: .moduleListScreen)

                if !state.didAppear {
                    state.didAppear = true
                    return .run { send in
                        await send(.onInit)
                    }
                }
                return .send(.subscribeToModules)
            case .onDisappear:
                if !navigation.contains(destination: .aisleModuleListView) {
                    return .run { send in
                        await send(.onDeinit)
                    }.cancellable(id: CancelID.module)
                }
                return .cancel(id: CancelID.module)
            case .subscribeToModules:
                let aisleId = state.aisle.id
                let storeId = state.aisle.store
                return .publisher {
                    aisleService.modulePublisher(storeId: storeId, forAisle: aisleId)
                        .replaceError(with: nil)
                        .compactMap { $0 }
                        .map { Action.updateModules(modules: $0, aisleId: aisleId) }
                }
                .cancellable(id: CancelID.list, cancelInFlight: true)
            case .onModuleButtonTapped(let module):
                let aisle = state.aisle
                return .run { send in
                    await send(.onModuleSelected(aisle: aisle, module: module))
                }
            case .updateModules(let modules, let aisleId):
                guard aisleId == state.aisle.id else {
                    // Ignore updates if from previously selected aisle.
                    return .none
                }
                state.modules = modules

                return .none
            case .onAddModuleButtonTapped:
                analytics.logMappingFlow(event: .newModuleButton)

                return .run { send in
                    await send(.createNewModule)
                }.cancellable(id: CancelID.module)
            case .createNewModule:
                let aisle = state.aisle
                return .run { send in
                    // First create the aisle if needed.
                    let persistedAisle = try await aisleService.persist(aisle: .existing(details: aisle))
                    let storeId = persistedAisle.store

                    let modules = aisleService.modules(forAisle: persistedAisle.id) ?? []
                    let nextModuleNumber = modules.inferNextModuleNumber()
                    let nextModuleOrder = (modules.compactMap(\.order).max() ?? 0) + 1
                    do {
                        let module = try aisleService.addModule(
                            "\(nextModuleNumber)",
                            forAisle: persistedAisle,
                            order: nextModuleOrder,
                            storeId: storeId
                        )

                        await send(
                            .onModuleCreated(aisle: persistedAisle, module: module)
                        )
                    } catch {
                        Log.general.error("Could not create a module: \(error.description)")
                        await send(.onError(error.description))
                    }
                }
            case .onModuleSelected:
                analytics.logMappingFlow(event: .moduleSelection)
                return .none
            case .onError:
                return .none
            case .onModuleCreated:
                return .none
            }
        }
    }
}

private extension [ShelfScanModule] {
    func inferNextModuleNumber() -> Int {
        var proposedNumber = count + 1
        while contains(where: { $0.moduleReference == "\(proposedNumber)" }) {
            proposedNumber += 1
        }
        return proposedNumber
    }
}

