    static var defaultdescription: (PersistedShelfScanModule, PersistedShelfScanAisle) -> String? = { _, _ in
        nil
    }

    static var defaultCheckmark: (PersistedShelfScanModule, PersistedShelfScanAisle) -> Bool = { _, _ in
        false
    }

    static func create(
        moduleDescriptionFor: @escaping (PersistedShelfScanModule, PersistedShelfScanAisle) -> String? = Self
            .defaultdescription,
        shouldShowCheckmarkFor: @escaping (PersistedShelfScanModule, PersistedShelfScanAisle) -> Bool = Self
            .defaultCheckmark
    ) -> (TCAStoreOf<AisleModuleListFeature>) -> AisleModuleListView {
        { store in
            AisleModuleListView(
                store: store,
                moduleDescriptionFor: moduleDescriptionFor,
                shouldShowCheckmarkFor: shouldShowCheckmarkFor
            )
        }
    }

#if DEBUG
    @ObserveInjection private var forceRedraw
#endif

    let store: TCAStoreOf<AisleModuleListFeature>

    var moduleDescriptionFor: (PersistedShelfScanModule, PersistedShelfScanAisle) -> String?

    var shouldShowCheckmarkFor: (PersistedShelfScanModule, PersistedShelfScanAisle) -> Bool

    var body: some View {
        VStack {
            List {
                Section {
                    ForEach(store.modules) { module in
                        Button {
                            store.send(.onModuleButtonTapped(module: module))
                        } label: {
                            row(module: module)
                        }
                    }
                }
            }

            if store.allowsModuleCreation {
                Section {
                    Button(L10n.Svm.AisleDetails.addModule) {
                        store.send(.onAddModuleButtonTapped)
                    }
                    .listRowInsets(EdgeInsets())
                    .scanditButtonStyle()
                }
            }
        }
        .onAppear {
            store.send(.onAppear)
        }
        .onDisappear {
            store.send(.onDisappear)
        }
        .scanditNavigationBar(title: store.aisleName)
        .enableInjection()
    }

    func row(module: PersistedShelfScanModule) -> some View {
        HStack {
            VStack(alignment: .leading, spacing: 2) {
                Text(module.reference)
                    .foregroundStyle(Colors.neutral90)
                if let description = moduleDescriptionFor(module, store.aisle) {
                    Text(description)
                        .font(Constants.Fonts.shelfAisleDetail)
                        .foregroundColor(Colors.Svm.aisleListGrey.swiftUIColor)
                }
            }
            .frame(maxWidth: .infinity, alignment: .leading)

            if shouldShowCheckmarkFor(module, store.aisle) {
                Images.checkmark.swiftui
                    .foregroundStyle(Colors.brandGreen100)
            }
        }
    }
}

